PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "phone" varchar, "address" varchar, "dob" date, "password" varchar not null, "usertype" varchar not null default 'user', "cafe_member" tinyint(1) not null default '0', "remember_token" varchar, "deleted_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "products" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "short_description" varchar, "description" text, "regular_price" numeric not null, "sale_price" numeric, "SKU" varchar not null, "stock_status" varchar check ("stock_status" in ('instock', 'outofstock')) not null, "featured" tinyint(1) not null default '0', "quantity" integer not null default '10', "image" varchar not null, "images" text, "category_id" integer, "created_at" datetime, "updated_at" datetime, foreign key("category_id") references "categories"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "orders" ("id" integer primary key autoincrement not null, "user_id" integer not null, "firstname" varchar not null, "lastname" varchar not null, "mobile" varchar not null, "email" varchar not null, "line1" varchar not null, "line2" varchar, "city" varchar not null, "postalcode" varchar not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "notifications" ("id" varchar not null, "type" varchar not null, "notifiable_type" varchar not null, "notifiable_id" integer not null, "data" text not null, "read_at" datetime, "created_at" datetime, "updated_at" datetime, primary key ("id"));
CREATE TABLE IF NOT EXISTS "business_hours" ("id" integer primary key autoincrement not null, "day" varchar not null, "from" time not null, "to" time not null, "step" integer not null default '60', "off" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "appointments" ("id" integer primary key autoincrement not null, "user_id" integer not null, "date" date not null, "time" time not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "games" ("id" integer primary key autoincrement not null, "name" varchar not null, "description" text, "genre" varchar, "release_date" date, "platform" varchar, "rating" integer, "publisher" varchar, "developer" varchar, "image" varchar, "multiplayer" tinyint(1) not null default '0', "price" numeric not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "order_details" ("id" integer primary key autoincrement not null, "order_id" integer not null, "order_type" varchar not null, "image" varchar not null, "product_name" varchar not null, "product_price" numeric not null, "quantity" integer not null, "total" numeric not null, "created_at" datetime, "updated_at" datetime, foreign key("order_id") references "orders"("id") on delete cascade);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE UNIQUE INDEX "users_phone_unique" on "users" ("phone");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
CREATE UNIQUE INDEX "categories_name_unique" on "categories" ("name");
CREATE UNIQUE INDEX "categories_slug_unique" on "categories" ("slug");
CREATE UNIQUE INDEX "products_name_unique" on "products" ("name");
CREATE UNIQUE INDEX "products_slug_unique" on "products" ("slug");
CREATE INDEX "notifications_notifiable_type_notifiable_id_index" on "notifications" ("notifiable_type", "notifiable_id");
CREATE UNIQUE INDEX "business_hours_day_unique" on "business_hours" ("day");
COMMIT;
